    private void btnLimpiarMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnLimpiarMouseClicked
       txtUsuario.setText("");
       txtContrasena.setText("");
       lblInformacion.setText("");
    }//GEN-LAST:event_btnLimpiarMouseClicked

    private void btnLoginMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnLoginMouseClicked
        String usuario = txtUsuario.getText();
        String pass = txtContrasena.getText();
        if(usuario.equals("pepito")) {
            if(pass.equals("educacionIT")) {
                lblInformacion.setText("Bienvenido pepito!");
            } else {
                lblInformacion.setText("El usuario no coincide con la contrase침a");
            }
        } else {
            lblInformacion.setText("El usuario no existe!");
        }
    }//GEN-LAST:event_btnLoginMouseClicked

